INSERT INTO lawfirm_qacategory VALUES(1,'سوالات و پاسخ‌ها','qa-general','سوالات و پاسخ‌های عمومی درباره موضوعات حقوقی','2025-12-01 20:58:19.117652');
INSERT INTO lawfirm_qacategory VALUES(2,'سوالات عمومی','general','سوالات و پاسخ‌های عمومی حقوقی','2025-12-01 21:48:31.091441');
INSERT INTO lawfirm_answer VALUES(1,'شرکت بیمه‌ای که از پرداخت خسارت خودداری کرده است، دفاع کنید. وکیل شما می‌تواند به شما کمک کند تا حقوق خود را در دادگاه یا از طریق مذاکره با شرکت بیمه‌ای، پیگیری کنید.\r\n\r\nوکیل شما می‌تواند به شما کمک کند تا:\r\n\r\nمدارک و اسناد لازم را جمع‌آوری کنید\r\nشکایت خود را در دادگاه ثبت کنید\r\nدر جلسات دادگاه حضور پیدا کنید و از حقوق شما دفاع کنید\r\nبا شرکت بیمه‌ای مذاکره کنید تا به توافق برسید\r\nهمچنین، وکیل شما می‌تواند به شما کمک کند تا از حقوق خود در برابر شرکت بیمه‌ای آگاه شوید و از طریق قانونی، خسارت خود را دریافت کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.199040','2025-12-01 20:58:19.199054',1);
INSERT INTO lawfirm_answer VALUES(2,'طبق قانون، مهریه به عنوان حق مالی زن شناخته می‌شود و به محض وقوع عقد نکاح به زن تعلق می‌گیرد. زن می‌تواند در هر زمانی پس از عقد درخواست مهریه کند.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.203850','2025-12-01 20:58:19.203861',2);
INSERT INTO lawfirm_answer VALUES(3,'برای اجرا گذاشتن سفته، ابتدا باید سفته را واخواست کنید. پس از واخواست، می‌توانید از طریق دادگاه درخواست اجراییه کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.208353','2025-12-01 20:58:19.208362',3);
INSERT INTO lawfirm_answer VALUES(4,'برای ثبت شرکت، باید ابتدا نوع شرکت را مشخص کنید (سهامی خاص، مسئولیت محدود و غیره)، سپس از طریق سامانه ثبت شرکت‌ها مدارک مورد نیاز را تهیه و ارسال کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.213417','2025-12-01 20:58:19.213429',4);
INSERT INTO lawfirm_answer VALUES(5,'اگر شوهر شما حاضر به طلاق نیست، شما می‌توانید با ارائه دلایلی مانند عسر و حرج (سختی در زندگی مشترک) یا عدم پرداخت نفقه، درخواست طلاق کنید. همچنین در صورت داشتن شروط ضمن عقد، می‌توانید از آن استفاده کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.218254','2025-12-01 20:58:19.218266',5);
INSERT INTO lawfirm_answer VALUES(6,'شما می‌توانید با مراجعه به دادگاه خانواده و ارائه دادخواست، درخواست نفقه معوقه کنید. نفقه یک حق قانونی برای زن است و شوهر موظف به پرداخت آن است.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.222682','2025-12-01 20:58:19.222692',6);
INSERT INTO lawfirm_answer VALUES(7,'برای پیگیری چک برگشتی، ابتدا باید گواهی عدم پرداخت از بانک دریافت کنید و سپس با ارائه آن به دادگاه، دادخواست مطالبه وجه چک را مطرح کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.227938','2025-12-01 20:58:19.227958',7);
INSERT INTO lawfirm_answer VALUES(8,'دیه مبلغی است که برای جبران خسارت جسمی یا جانی به فرد پرداخت می‌شود. میزان دیه بر اساس نوع جراحت یا فوت توسط پزشکی قانونی تعیین و به نرخ روز محاسبه می‌شود.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.232823','2025-12-01 20:58:19.232834',8);
INSERT INTO lawfirm_answer VALUES(9,'شما می‌توانید با مراجعه به شورای حل اختلاف درخواست تخلیه مستاجر را مطرح کنید. پس از صدور حکم تخلیه، اگر مستاجر همچنان اجاره را پرداخت نکند، حکم تخلیه اجرا می‌شود.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.237442','2025-12-01 20:58:19.237451',9);
INSERT INTO lawfirm_answer VALUES(10,'تقسیم ارث بر اساس قانون مدنی ایران انجام می‌شود. سهم هر وارث بر اساس نسبت خویشاوندی با متوفی و طبقه ارث مشخص می‌شود. ابتدا دیون و وصایای متوفی کسر می‌شود و سپس ارث میان ورثه تقسیم می‌گردد.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.242056','2025-12-01 20:58:19.242068',10);
INSERT INTO lawfirm_answer VALUES(11,'بله، شما می‌توانید به اداره کار مراجعه کرده و شکایت خود را ثبت کنید. اداره کار وظیفه دارد به مشکلات مربوط به عدم پرداخت حقوق رسیدگی کند و کارفرما را ملزم به پرداخت معوقات کند.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.247324','2025-12-01 20:58:19.247334',11);
INSERT INTO lawfirm_answer VALUES(12,'مهریه عندالمطالبه به این معناست که زن می‌تواند هر زمان که بخواهد بعد از عقد نکاح، مهریه خود را طلب کند و مرد موظف به پرداخت آن است.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.252278','2025-12-01 20:58:19.252289',12);
INSERT INTO lawfirm_answer VALUES(13,'گواهی عدم سازش سندی است که نشان می‌دهد تلاش برای سازش میان زن و شوهر به نتیجه نرسیده است. این گواهی توسط دادگاه صادر می‌شود و لازمه آغاز مراحل طلاق است.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.257058','2025-12-01 20:58:19.257069',13);
INSERT INTO lawfirm_answer VALUES(14,'در صورت جدایی، حضانت فرزند تا سن ۷ سالگی معمولاً با مادر است. بعد از این سن، حضانت با پدر خواهد بود مگر اینکه دادگاه به نفع مادر حکم دهد.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.262342','2025-12-01 20:58:19.262355',14);
INSERT INTO lawfirm_answer VALUES(15,'اجرت‌المثل به معنای دستمزدی است که زن به دلیل کارهایی که در خانه شوهر انجام داده، دریافت می‌کند. این حق در صورتی به زن تعلق می‌گیرد که درخواست طلاق از سوی مرد باشد و زن بدون دریافت مزد در منزل شوهر کار کرده باشد.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.267499','2025-12-01 20:58:19.267510',15);
INSERT INTO lawfirm_answer VALUES(16,'خیر، بدون داشتن کارت پایان خدمت یا معافیت از خدمت نظام وظیفه، معمولاً امکان دریافت گذرنامه وجود ندارد. شما باید ابتدا وضعیت خدمت نظام وظیفه خود را تعیین کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.272327','2025-12-01 20:58:19.272339',16);
INSERT INTO lawfirm_answer VALUES(17,'اگر کارفرما برای شما حق بیمه پرداخت نکرده است، می‌توانید به شعبه تامین اجتماعی محل کار خود مراجعه کرده و شکایت کنید. سازمان تامین اجتماعی کارفرما را ملزم به پرداخت حق بیمه خواهد کرد.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.280201','2025-12-01 20:58:19.280213',17);
INSERT INTO lawfirm_answer VALUES(18,'برای شکایت از مزاحم تلفنی، باید به پلیس فتا یا دادسرای جرایم رایانه‌ای مراجعه کنید و شکایت خود را ثبت کنید. با پیگیری قانونی، شماره تلفن فرد مزاحم قابل شناسایی و پیگیری خواهد بود.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.285034','2025-12-01 20:58:19.285045',18);
INSERT INTO lawfirm_answer VALUES(19,'پناهندگی به معنای درخواست حمایت از یک کشور دیگر به دلیل ترس از آزار یا خطر در کشور مبدا است. برای گرفتن پناهندگی، باید به کشور مقصد درخواست دهید و دلایل قانع‌کننده برای پناهندگی ارائه کنید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.289822','2025-12-01 20:58:19.289833',19);
INSERT INTO lawfirm_answer VALUES(20,'برای دریافت تابعیت ایران، باید حداقل ۵ سال در ایران اقامت داشته باشید و درخواست خود را به وزارت کشور ارائه کنید. همچنین باید شرایط لازم مانند داشتن شغل قانونی و عدم سوءپیشینه را نیز داشته باشید.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.295351','2025-12-01 20:58:19.295364',20);
INSERT INTO lawfirm_answer VALUES(21,'در صورت تصادف بدون بیمه، شما مسئول پرداخت تمامی خسارات به طرف مقابل خواهید بود. همچنین ممکن است جریمه نقدی و محرومیت‌های دیگری نیز به شما تحمیل شود.','موسسه حقوقی دادگان','متخصص حقوق',0,0,1,'2025-12-01 20:58:19.300256','2025-12-01 20:58:19.300266',21);
INSERT INTO lawfirm_answer VALUES(22,unistr('شرکت بیمه‌ای که از پرداخت خسارت خودداری کرده است، دفاع کنید. وکیل شما می‌تواند به شما کمک کند تا حقوق خود را در دادگاه یا از طریق مذاکره با شرکت بیمه‌ای، پیگیری کنید.\u000d\u000a\u000d\u000aوکیل شما می‌تواند به شما کمک کند تا:\u000d\u000a\u000d\u000aمدارک و اسناد لازم را جمع‌آوری کنید\u000d\u000aشکایت خود را در دادگاه ثبت کنید\u000d\u000aدر جلسات دادگاه حضور پیدا کنید و از حقوق شما دفاع کنید\u000d\u000aبا شرکت بیمه‌ای مذاکره کنید تا به توافق برسید\u000d\u000aهمچنین، وکیل شما می‌تواند به شما کمک کند تا از حقوق خود در برابر شرکت بیمه‌ای آگاه شوید و از طریق قانونی، خسارت خود را دریافت کنید.'),'موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.205859','2025-12-01 21:48:31.205874',30);
INSERT INTO lawfirm_answer VALUES(23,'طبق قانون، مهریه به عنوان حق مالی زن شناخته می‌شود و به محض وقوع عقد نکاح به زن تعلق می‌گیرد. زن می‌تواند در هر زمانی پس از عقد درخواست مهریه کند.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.216587','2025-12-01 21:48:31.216604',31);
INSERT INTO lawfirm_answer VALUES(24,'برای اجرا گذاشتن سفته، ابتدا باید سفته را واخواست کنید. پس از واخواست، می‌توانید از طریق دادگاه درخواست اجراییه کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.225092','2025-12-01 21:48:31.225108',32);
INSERT INTO lawfirm_answer VALUES(25,'برای ثبت شرکت، باید ابتدا نوع شرکت را مشخص کنید (سهامی خاص، مسئولیت محدود و غیره)، سپس از طریق سامانه ثبت شرکت‌ها مدارک مورد نیاز را تهیه و ارسال کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.235157','2025-12-01 21:48:31.235176',33);
INSERT INTO lawfirm_answer VALUES(26,'اگر شوهر شما حاضر به طلاق نیست، شما می‌توانید با ارائه دلایلی مانند عسر و حرج (سختی در زندگی مشترک) یا عدم پرداخت نفقه، درخواست طلاق کنید. همچنین در صورت داشتن شروط ضمن عقد، می‌توانید از آن استفاده کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.243214','2025-12-01 21:48:31.243237',34);
INSERT INTO lawfirm_answer VALUES(27,'شما می‌توانید با مراجعه به دادگاه خانواده و ارائه دادخواست، درخواست نفقه معوقه کنید. نفقه یک حق قانونی برای زن است و شوهر موظف به پرداخت آن است.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.253259','2025-12-01 21:48:31.253278',35);
INSERT INTO lawfirm_answer VALUES(28,'برای پیگیری چک برگشتی، ابتدا باید گواهی عدم پرداخت از بانک دریافت کنید و سپس با ارائه آن به دادگاه، دادخواست مطالبه وجه چک را مطرح کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.263070','2025-12-01 21:48:31.263098',36);
INSERT INTO lawfirm_answer VALUES(29,'دیه مبلغی است که برای جبران خسارت جسمی یا جانی به فرد پرداخت می‌شود. میزان دیه بر اساس نوع جراحت یا فوت توسط پزشکی قانونی تعیین و به نرخ روز محاسبه می‌شود.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.272304','2025-12-01 21:48:31.272321',37);
INSERT INTO lawfirm_answer VALUES(30,'شما می‌توانید با مراجعه به شورای حل اختلاف درخواست تخلیه مستاجر را مطرح کنید. پس از صدور حکم تخلیه، اگر مستاجر همچنان اجاره را پرداخت نکند، حکم تخلیه اجرا می‌شود.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.283453','2025-12-01 21:48:31.283479',38);
INSERT INTO lawfirm_answer VALUES(31,'تقسیم ارث بر اساس قانون مدنی ایران انجام می‌شود. سهم هر وارث بر اساس نسبت خویشاوندی با متوفی و طبقه ارث مشخص می‌شود. ابتدا دیون و وصایای متوفی کسر می‌شود و سپس ارث میان ورثه تقسیم می‌گردد.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.291504','2025-12-01 21:48:31.291522',39);
INSERT INTO lawfirm_answer VALUES(32,'بله، شما می‌توانید به اداره کار مراجعه کرده و شکایت خود را ثبت کنید. اداره کار وظیفه دارد به مشکلات مربوط به عدم پرداخت حقوق رسیدگی کند و کارفرما را ملزم به پرداخت معوقات کند.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.301601','2025-12-01 21:48:31.301618',40);
INSERT INTO lawfirm_answer VALUES(33,'مهریه عندالمطالبه به این معناست که زن می‌تواند هر زمان که بخواهد بعد از عقد نکاح، مهریه خود را طلب کند و مرد موظف به پرداخت آن است.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.310265','2025-12-01 21:48:31.310309',41);
INSERT INTO lawfirm_answer VALUES(34,'گواهی عدم سازش سندی است که نشان می‌دهد تلاش برای سازش میان زن و شوهر به نتیجه نرسیده است. این گواهی توسط دادگاه صادر می‌شود و لازمه آغاز مراحل طلاق است.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.318813','2025-12-01 21:48:31.318832',42);
INSERT INTO lawfirm_answer VALUES(35,'در صورت جدایی، حضانت فرزند تا سن ۷ سالگی معمولاً با مادر است. بعد از این سن، حضانت با پدر خواهد بود مگر اینکه دادگاه به نفع مادر حکم دهد.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.328612','2025-12-01 21:48:31.328637',43);
INSERT INTO lawfirm_answer VALUES(36,'اجرت‌المثل به معنای دستمزدی است که زن به دلیل کارهایی که در خانه شوهر انجام داده، دریافت می‌کند. این حق در صورتی به زن تعلق می‌گیرد که درخواست طلاق از سوی مرد باشد و زن بدون دریافت مزد در منزل شوهر کار کرده باشد.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.336795','2025-12-01 21:48:31.336812',44);
INSERT INTO lawfirm_answer VALUES(37,'خیر، بدون داشتن کارت پایان خدمت یا معافیت از خدمت نظام وظیفه، معمولاً امکان دریافت گذرنامه وجود ندارد. شما باید ابتدا وضعیت خدمت نظام وظیفه خود را تعیین کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.346482','2025-12-01 21:48:31.346501',45);
INSERT INTO lawfirm_answer VALUES(38,'اگر کارفرما برای شما حق بیمه پرداخت نکرده است، می‌توانید به شعبه تامین اجتماعی محل کار خود مراجعه کرده و شکایت کنید. سازمان تامین اجتماعی کارفرما را ملزم به پرداخت حق بیمه خواهد کرد.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.354645','2025-12-01 21:48:31.354661',46);
INSERT INTO lawfirm_answer VALUES(39,'برای شکایت از مزاحم تلفنی، باید به پلیس فتا یا دادسرای جرایم رایانه‌ای مراجعه کنید و شکایت خود را ثبت کنید. با پیگیری قانونی، شماره تلفن فرد مزاحم قابل شناسایی و پیگیری خواهد بود.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.364342','2025-12-01 21:48:31.364360',47);
INSERT INTO lawfirm_answer VALUES(40,'پناهندگی به معنای درخواست حمایت از یک کشور دیگر به دلیل ترس از آزار یا خطر در کشور مبدا است. برای گرفتن پناهندگی، باید به کشور مقصد درخواست دهید و دلایل قانع‌کننده برای پناهندگی ارائه کنید.','موسسه حقوقی دادگان','مشاور حقوقی',0,1,1,'2025-12-01 21:48:31.372876','2025-12-01 21:48:31.372895',48);
