PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "auth_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "auth_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
CREATE TABLE IF NOT EXISTS "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "first_name" varchar(150) NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_category" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "slug" varchar(100) NOT NULL UNIQUE, "description" text NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_consultationtype" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "price" integer unsigned NOT NULL CHECK ("price" >= 0), "duration" integer unsigned NOT NULL CHECK ("duration" >= 0), "description" text NOT NULL, "is_active" bool NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_contactmessage" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "full_name" varchar(100) NOT NULL, "phone" varchar(15) NOT NULL, "email" varchar(254) NOT NULL, "subject" text NOT NULL, "is_read" bool NOT NULL, "admin_response" text NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_qacategory" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "slug" varchar(100) NOT NULL UNIQUE, "description" text NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_sitesettings" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "site_title" varchar(200) NOT NULL, "site_description" text NOT NULL, "phone" varchar(15) NOT NULL, "email" varchar(254) NOT NULL, "address" text NOT NULL, "logo" varchar(100) NULL, "consultation_count" integer unsigned NOT NULL CHECK ("consultation_count" >= 0), "success_cases" integer unsigned NOT NULL CHECK ("success_cases" >= 0), "contracts_count" integer unsigned NOT NULL CHECK ("contracts_count" >= 0), "experience_years" integer unsigned NOT NULL CHECK ("experience_years" >= 0));
CREATE TABLE IF NOT EXISTS "lawfirm_testimonial" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "title" varchar(100) NOT NULL, "content" text NOT NULL, "rating" integer unsigned NOT NULL CHECK ("rating" >= 0), "image" varchar(100) NULL, "is_published" bool NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_consultationrequest" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "full_name" varchar(100) NOT NULL, "phone" varchar(15) NOT NULL, "email" varchar(254) NOT NULL, "field" varchar(20) NOT NULL, "description" text NOT NULL, "preferred_date" datetime NULL, "status" varchar(20) NOT NULL, "admin_notes" text NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "consultation_type_id" bigint NOT NULL REFERENCES "lawfirm_consultationtype" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "lawfirm_answer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content" text NOT NULL, "answerer_name" varchar(100) NOT NULL, "answerer_title" varchar(100) NOT NULL, "votes" integer NOT NULL, "is_best_answer" bool NOT NULL, "is_published" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "question_id" bigint NOT NULL REFERENCES "lawfirm_question" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_blogpost" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL, "slug" varchar(200) NOT NULL UNIQUE, "excerpt" text NOT NULL, "content" text NOT NULL, "image" varchar(100) NULL, "published" bool NOT NULL, "featured" bool NOT NULL, "views" integer unsigned NOT NULL CHECK ("views" >= 0), "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "author_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "category_id" bigint NOT NULL REFERENCES "lawfirm_category" ("id") DEFERRABLE INITIALLY DEFERRED, "seo_description" varchar(160) NOT NULL, "seo_keywords" varchar(255) NOT NULL, "seo_title" varchar(60) NOT NULL);
CREATE TABLE IF NOT EXISTS "lawfirm_question" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL, "slug" varchar(200) NOT NULL UNIQUE, "content" text NOT NULL, "asker_name" varchar(100) NOT NULL, "asker_email" varchar(254) NOT NULL, "views" integer unsigned NOT NULL CHECK ("views" >= 0), "votes" integer NOT NULL, "is_answered" bool NOT NULL, "is_published" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "category_id" bigint NOT NULL REFERENCES "lawfirm_qacategory" ("id") DEFERRABLE INITIALLY DEFERRED, "seo_description" varchar(160) NOT NULL, "seo_keywords" varchar(255) NOT NULL, "seo_title" varchar(60) NOT NULL);
PRAGMA writable_schema=ON;
CREATE TABLE IF NOT EXISTS sqlite_sequence(name,seq);
DELETE FROM sqlite_sequence;
