CREATE INDEX "lawfirm_consultationrequest_consultation_type_id_1ff452b3" ON "lawfirm_consultationrequest" ("consultation_type_id");
CREATE INDEX "lawfirm_answer_question_id_924147af" ON "lawfirm_answer" ("question_id");
CREATE INDEX "django_session_expire_date_a5c62663" ON "django_session" ("expire_date");
